import React, { useState, setState, Component } from "react";
// import { View, FlatList, StyleSheet, Text, Dimensions } from "react-native";
// import * as React from 'react';  
import styles from "./App.css";
import getQuestions from './components/questions.js'
import getPrompts from "./components/medicalTexts";
// import getPopup from "./components/popup";
// import alertMode from "./components/alertMode";
import PopupAlert from "./components/popupAlert";


function App() {
  // Properties
  const [showResults, setShowResults] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showPopups, setShowPopup] = useState(true);
  const [key] = useState(0);
  const responses = [];
  const [score, setScore] = useState(0);

  const questions = getQuestions();
  const prompts = getPrompts();
  // const popup = getPopup();

  // Helper Functions

  const handleClose = () => setShowPopup(false);
  const handleShow = () => setShowPopup(true);

  class AnswerButton extends Component {
    constructor(props) {
      super(props);
      // this.state = {
      //   history: [

      //   ]
      // }
    }

    // addHistory = (key) => {
    //   this.state.history = [...this.state.history, {id: question_id, key: key}]
    //   console.log(this.state.history)
    // }

    /* A possible answer was clicked */
    optionClicked() {
      // TODO: make a dict with keys as question idxes and values as responses
      console.log({question_id: currentQuestion, answer_key: this.props.option.id})
      if (currentQuestion + 1 < prompts.length) {
        setCurrentQuestion(currentQuestion + 1);
        setShowPopup(true);
        console.log(showPopups)
        // console.log(showPopups);
      } else {
        setShowResults(true);
      }
    };

    render() {

      return (
        <li
          key={this.props.option.id}
          onClick={() => this.optionClicked()}
        >
          {this.props.option.text}
        </li>);

    }

  }

  /* Resets the game back to default */
  const restartGame = () => {
    // setScore(0);
    setCurrentQuestion(0);
    setShowResults(false);
  };

  return (
    <div className="App">
      {/* 1. Header  */}
      <h1 className='title'> Medical AI </h1>

      {/* 2. Show finished page or show the question game  */}
      {showResults ? (

      /* 3. Final Results */
      <div className="final-results">
        <h1>Thank you for taking the survey!</h1>
        <button onClick={() => restartGame()}>Restart game</button>
      </div>
      ) : (
        // showPopups ? (<PopupAlert showPopupMode={showPopups} />
        //   ) 


          <div className='Main'>
            <div className={styles.gptBox}>
              <div className="prompt-box">
                <div className="gpt-image"> <img src='./Person-outline.png' alt="Person" style={{ minWidth: 100 }}></img></div>
                <h2 className="gpt-text">{prompts[currentQuestion].prompt}</h2>
              </div>
              <div className="medical-text-box">
                <div className="gpt-image"> <img src='./GPT-logo.jpeg' alt="gpt Logo" style={{ minWidth: 100 }}></img></div>
                {/* <alertMode state="Mode1"></alertMode> */}
                <h3 className="gpt-text">{prompts[currentQuestion].text}</h3>
              </div>
            </div>
          </div>,


          // TODO: Add button to trigger popup

          // <div>
          //     {showPopups
          //       ? popup
          //       :  <hi />
          //     }
          //   </div>,

          /* 5. Question Card  */
          <div className="question-card">
            {/* Current Question  */}
            <h2 className="question-text">
              Question: {currentQuestion + 1} out of {prompts.length}
            </h2>
            <h3 className="question-text">{questions[currentQuestion].text}</h3>

            {/* List of possible answers  */}
            <ul>
              {questions[currentQuestion].options.map((option) => {
                return (
                  <AnswerButton option={option} />
                );
              })}
            </ul>
          </div>
        )}
    </div>
  );
}

export default App;
